import streamlit as st
import pandas as pd

st.write("Here's our first attempt at using data to create a table:")
st.write("I can do anything !")

images = ["https://b2052345.smushcdn.com/2052345/wp-content/uploads/2022/01/venice-italy-750x450.jpg?lossy=1&strip=1&webp=1", "https://www.touropia.com/gfx/b/2018/07/best_cities_italy.jpg", "https://travel.usnews.com/dims4/USNEWS/e07353f/2147483647/resize/445x280%5E%3E/crop/445x280/quality/85/?url=https%3A%2F%2Ftravel.usnews.com%2Fimages%2Fmain_image_cropped_445x280_zWEBjoE.jpg" ]
cols = st.columns(len(images))

# Loop through images and display them in respective columns
for i, image in enumerate(images):
    with cols[i]:
        st.image(image)

st.write("Venice is unique environmentally, architecturally, and historically, and in its days as a republic the city was styled la serenissima (“sublime”).")

zoom_levels = {
  "close_up": "data:image/jpeg;base64,",
  "wide_view": "https://images.ctfassets.net/i3kf1olze1gn/41k9o61CJJ5jIydrEVkB55/8b94be00975b636a094b7d755fa9f019/venice.jpg"
}

# Default zoom level
current_zoom = "close_up"

# User interaction buttons (replace with actual buttons)
if st.button("Zoom In"):
  current_zoom = "close_up"
if st.button("Zoom Out"):
  current_zoom = "wide_view"

# Display the image based on current zoom level
st.image(zoom_levels[current_zoom])
